import Header from "../components/Header";
import axios from "axios";
import getTimePassed from "../helpers/getTimePassed";

const Top = props => {
  console.log(props.topStories);

  return (
    <div style={{ backgroundColor: "#f2f3f5" }}>
      <Header />
      <h2 style={{ textAlign: "center" }}> Top Stories</h2>
      <div style={{ width: "80%", margin: "auto", backgroundColor: "white" }}>
        {props.topStories.map(renderStory)}
      </div>
    </div>
  );
};

const renderStory = story => {
  const { score, title, by, time, kids, id } = story;
  return (
    <div
      key={id}
      className="storyContainer"
      // style={{
      //   display: "flex",
      //   flexDirection: "row",
      //   borderBottom: "1px solid #f2f3f5",
      //   padding: ".75em"
      // }}
    >
      <p
        style={{
          color: "#f60",
          fontWeight: "bold",
          fontSize: "1.5em",
          alignSelf: "center",
          margin: "1rem"
        }}
      >
        {score}
      </p>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          margin: "1rem",
          justifyContent: "center"
        }}
      >
        {title.split("Ask HN:")[1]}
        <div
          style={{
            display: "flex",
            flexDirection: "row"
          }}
        >
          <p style={{ opacity: 0.75, margin: "0px" }}>
            by {by} {getTimePassed(time)} | {kids && `${kids.length} comments`}
          </p>
        </div>
      </div>
      {styles()}
    </div>
  );
};

const styles = () => (
  <style jsx global>
    {`
      .storyContainer {
        display: flex;
        flex-direction: row;
        border-bottom: 1px solid #f2f3f5;
        padding: 0.75em;
      }
    `}
  </style>
);

export async function getStaticProps() {
  const res = await axios.get(
    "https://hacker-news.firebaseio.com/v0/askstories.json"
  );
  let promises = res.data.map(id => {
    return axios.get(`https://hacker-news.firebaseio.com/v0/item/${id}.json`);
  });
  const result = await Promise.all(promises);

  return {
    props: {
      topStories: result.map(i => i.data)
    }
  };
}
export default Top;
