import Header from "../components/Header";
import axios from "axios";

const Top = props => {
  console.log(props.topStories);

  return (
    <div>
      <Header />
      <h2> Top Stories</h2>
      {/* {props.topStories.map(renderStory)} */}
      {renderStory(props.topStories[0])}
    </div>
  );
};

const renderStory = story => {
  const { score, title, by, time, kids, id } = story;
  return (
    <div key={id} className="storyContainer">
      {score} {title} {by} {time} {kids && `${kids.length} comments`}
    </div>
  );
};

const styles = () => (
  <style jsx global>
    {`
      .storyContainer {
        display: flex;
      }
    `}
  </style>
);

export async function getStaticProps() {
  const res = await axios.get(
    "https://hacker-news.firebaseio.com/v0/askstories.json"
  );
  let promises = res.data.map(id => {
    return axios.get(`https://hacker-news.firebaseio.com/v0/item/${id}.json`);
  });
  const result = await Promise.all(promises);

  return {
    props: {
      topStories: result.map(i => i.data)
    }
  };
}
export default Top;
